          import sailpoint.object.WorkItem;
          import sailpoint.object.Form;
          import sailpoint.object.Field;
          import java.util.Iterator;
          import java.util.List;
          import java.util.ArrayList;
          import sailpoint.object.Attributes;
          import sailpoint.tools.Util;
          if (workItemComments == null)
            workItemComments = new ArrayList(); 
          Form customForm = item.getForm();
          Iterator<Field> fieldIterator = customForm.iterateFields();
          Attributes formAttrs = new Attributes();
          while (fieldIterator.hasNext()) {
            Field field = fieldIterator.next();
            if (!field.isReadOnly()) {
              String name = field.getName();
              Object value = field.getValue();
              if(value!=null) {
                if(value instanceof List) {
                  value = Util.listToCsv(value);
                }
                workItemComments.add(name + "=" + value);
              }

              formAttrs.put("IIQEXTENDEDFORMIIQ" + name, value);
            } 
          } 
          workflow.put(formAttVar, formAttrs);
          workflow.put(modifyProjectVar, Boolean.valueOf(true));

